  //   let val = 'faire un choix'
  //   Object.assign(d, {[data[key]]: data[val]})
  //   setDict(d)
  // })

  const[word, setWord] = useState("")
  const[message, setMessage] = useState("")

  let dict = {
    "make a choice": "faire un choix",
    'make an offer':'faire une offre',
    'make a commitment':'prendre un engagement',
    'make one\'s escape':'prendre la fuite',
  }

  const handleEnter = (e) => {
    console.log(e.target.key)
    setWord(e.target.value)
  }

  const search = (e) => {
    if (e.key === 'Enter') {
      console.log(word)
      if (dict[word] === undefined) {
        setMessage("Word Not In Dictionary")
      }
      else {
        var arr = word.split(/\s+/);
        console.log(arr)
      }
    }
  }


  return (
    <div className='App'>
      <div className="navbar navbar-light">
        <img src = {logo} id='logo'></img>
      </div>

      <div className='content'>
        {/* <h2> Creator: 大聪明</h2>
        <p> ⬇️</p> */}
        {/* <img src = {profileImg} id='profile'></img> */}

        <h2>Enter an English Word</h2>
        <input 
          className="form-control" 
          placeholder={"Press Enter to Search"}
          value={word}
          onChange = {(e) => handleEnter(e)}
          onKeyDown = {search}
        ></input>
      </div>
    </div>

  );
}

export default App;
